    <input type="hidden" name="_token" value="<?php echo csrf_token(); ?>"/>

    <script language="javascript">

        loadOrderList();

        $(".right-nav-menu-toggle").on("click",function(e){
        $('.right-nav-menu').toggleClass('open');
        });

        function loadOrderList(){
            $('.order-list').html('');
            $('.order_loader').css({"display":"block"});
            $.ajax({
                url: '{{ url('/get-my-order-list') }}',
                type: "POST",
                //dataType: 'json',
                headers: {
                    'X-CSRF-TOKEN': '{{ csrf_token() }}'
                },
                data: {},
                success: function (response) {

                    $('.order_loader').css({"display":"none"});

                    if (response.responseCode == 1) {
                        $('.order-list').css({"display":"block"});
                        $('.order-list').html(response.html);
                    }

                    (function ($) {
                        "use strict";

                        var $qty = $(this).closest('.price-increase-decrese-group').find('.input-number');
                        var currentVal = $qty.val();
                        if(currentVal === 0){
                            alert('hi')
                            // $(this).closest('.cart-btn-toggle').find('.cart-btn').show()
                            $(this).closest('.cart-btn-toggle').find('.price-btn').hide()
                        }

                        $(".wish-link").on("click",function(e){
                            e.preventDefault();
                            $(this).toggleClass("focus");
                            // $("p").toggleClass("main");
                        });

                        $(".all-catagory-option > a").on("click",function(e){
                            $('.page-layout').toggleClass('open-side-menu')
                            $('body').toggleClass('open-side-menu')
                            // $(this).toggleClass('open-bar')
                        });
                        var contentwidth = jQuery(window).width();
                        if ((contentwidth) > '1200') {
                            $('.home-layout').addClass('open-side-menu')
                        }
                        if ((contentwidth) > '1200') {
                            $('.sticky-sidebar-home').addClass('open-side-menu')
                        }
                        if ((contentwidth) < '991') {
                            $('.widget .widget-wrapper').addClass('collapse')
                            $('.widget-callapse').addClass('collapse')
                        }

                        if ((contentwidth) < '991') {
                            $('.cart-btn-toggle').removeAttr('onclick');
                        }


                        $('.cart-product-item>.close-item').on('click',function(){
                            $(this).parent('.cart-product-item').remove();
                        })

                        $('.wishlist-item>.close-item').on('click',function(){
                            $(this).parent('.wishlist-item').remove();
                        })


                        // fixed menu app home page
                        $(window).on("scroll",function() {
                            var scroll = $(window).scrollTop();

                            if (scroll >= 100) {
                                $(".header-bottom,.mobile-header,.catagory-sidebar-area").addClass("fixed-totop animated slideInDown");
                            } else {
                                $(".header-bottom,.mobile-header,.catagory-sidebar-area").removeClass("fixed-totop  animated slideInDown");
                            }
                        });


                        $('.view').on('click',function(){
                            // $(this).text("Show Less");
                            $(this).parents('.order-card').addClass("show")
                        });
                        $('.show-less').on('click',function(){
                            // $(this).text("Show Less");
                            $(this).parents('.order-card').removeClass("show")
                        });



                        $(".right-nav-menu-toggle").on("click",function(e){
                            $('.right-nav-menu').toggleClass('open');
                        });

                    })(jQuery);
                }
            });
        }

        $(".myProfileBtn").on("click",function(e){
            $('.order-list').html('');
            $(".myProfileBtn").parent('li').addClass("active");
            $(".myOrderBtn").parent('li').removeClass("active");
            $('.order_loader').css({"display":"block"});
            $.ajax({
                url: '{{ url('/get-my-profile-data') }}',
                type: "POST",
                //dataType: 'json',
                headers: {
                    'X-CSRF-TOKEN': '{{ csrf_token() }}'
                },
                data: {},
                success: function (response) {

                    $('.order_loader').css({"display":"none"});

                    if (response.responseCode == 1) {
                        $('.order-list').css({"display":"block"});
                        $('.order-list').html(response.html);
                    }

                }
            });
        });

        $(".myOrderBtn").on("click",function(e){
            $(".myOrderBtn").parent('li').addClass("active");
            $(".myProfileBtn").parent('li').removeClass("active");
            loadOrderList();
        });

        $(document).on("click", ".updateProfileBtn", function() {
            $('.profile_message_section').html('');
            var btn = jQuery(this);
            var btn_content = btn.html();
            btn.html('<i class="fa fa-spinner fa-spin"></i>');
            btn.prop('disabled', true);

            var first_name = $('.first_name').val();
            var last_name = $('.last_name').val();
            var house_no = $('.house_no').val();
            var street_name = $('.street_name').val();
            var postcode = $('.postcode').val();
            var area = $('.area').val();
            var city = $('.city').val();
            var country = $('.country').val();
            var mobile_no = $('.mobile_no').val();

            $.ajax({
                url: '{{ url('/update-profile-data') }}',
                type: "POST",
                //dataType: 'json',
                headers: {
                    'X-CSRF-TOKEN': '{{ csrf_token() }}'
                },
                data: {
                    first_name: first_name,
                    last_name: last_name,
                    house_no: house_no,
                    street_name: street_name,
                    postcode: postcode,
                    area: area,
                    city: city,
                    country: country,
                    mobile_no: mobile_no,
                },
                success: function (response) {

                    btn.html(btn_content);
                    btn.prop('disabled', false);
                    if (response.responseCode == 1) {
                        $('.profile_message_section').html('<span style="color: #0d3625;font-weight: bold;">' + response.message + '</span>');
                    } else {
                        $('.profile_message_section').html('<span style="color: red;font-weight: bold;">' + response.message + '</span>');
                    }

                }
            });
        });
    </script>
